                String mid,name,mobno,address,city,dob,sql;
                mid=l1.getText();
                name=TF2.getText();
                mobno =TF3.getText();
                address=TA1.getText();
                city=TF5.getText();
                dob=TF6.getText();
                sql="insert into member values("+mid+",'"+name+"',"+mobno+",'"+address+"','"+city+"','"+dob+"')";
