    private void TF2FocusGained(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_TF2FocusGained
{
            try{
                Class.forName("java.sql.DriverManager");
                Connection con=DriverManager.getConnection("jdbc:mysql://Localhost:3306/tupperware","root","root");
                String sql,memid="";
                sql="select ifnull(max(mid),0) from member;";
                 Statement st=con.createStatement();

                  ResultSet rs=st.executeQuery(sql);
                if(rs.next())
                 memid=rs.getString("ifnull(max(mid),0)");
                  int mid=Integer.parseInt(memid);
                  System.out.print(""+mid);
                  l1.setText(""+(mid+1));
                   }
            catch(Exception e)
            {
                JOptionPane.showMessageDialog(null,e.getMessage());
            }
        }

        // TODO add your handling code here:
    }//GEN-LAST:event_TF2FocusGained

